function getScrollWidth(){return window.innerWidth-document.body.clientWidth}function toggleScrollBody(){document.querySelector("body").classList.toggle("overflow-hidden")}function compensationScroll(){const t=document.querySelector(".wrapper");t.hasAttribute("style")?t.removeAttribute("style"):t.style.marginRight=getScrollWidth()+"px"}function addEventOnKeydown(t,e){document.addEventListener("keydown",(function(n){n.code===t&&e()}))}