function toggleMenu(){const e=document.querySelector("body"),t=document.querySelector(".wrapper"),o=document.querySelector(".burger"),n=document.querySelector(".main-menu__content");o.addEventListener("click",()=>{o.classList.toggle("active"),n.classList.toggle("active"),t.hasAttribute("style")?t.removeAttribute("style"):t.style.marginRight=getScrollWidth()+"px",e.classList.toggle("overflow-hidden")})}function getScrollWidth(){return window.innerWidth-document.body.clientWidth}toggleMenu();